-- Insert sample Persian quotes from famous poets
INSERT INTO public.persian_quotes (text_persian, text_english, poet, poet_english, source, category, tags) VALUES
('عاشقان مرده‌اند در عشق زنده / تا ابد در دل جانان پاینده', 'Lovers are dead in love, alive / Forever enduring in the beloved''s heart', 'مولانا جلال‌الدین رومی', 'Rumi', 'مثنوی معنوی', 'عشق', ARRAY['عشق', 'زندگی', 'جاودانگی']),

('غزل گفتن آسان نمود اول / ولی طاق کردن خم مشکل است', 'Writing ghazals seemed easy at first / But bending the curve is difficult', 'حافظ شیرازی', 'Hafez', 'دیوان حافظ', 'حکمت', ARRAY['شعر', 'هنر', 'مهارت']),

('بنی آدم اعضای یک پیکرند / که در آفرینش ز یک گوهرند', 'Human beings are members of a whole / In creation of one essence and soul', 'سعدی شیرازی', 'Saadi', 'گلستان', 'حکمت', ARRAY['انسانیت', 'وحدت', 'برادری']),

('چو ایران نباشد تن من مباد / بدین بوم و بر زنده یک تن مباد', 'If Iran does not exist, may my body not exist / May no one live on this earth and land', 'فردوسی', 'Ferdowsi', 'شاهنامه', 'وطن', ARRAY['میهن', 'عشق به وطن', 'فداکاری']),

('نان که نداری غم مخور که هست / آب که نداری غم مخور که هست', 'If you have no bread, don''t worry, there is / If you have no water, don''t worry, there is', 'عمر خیام', 'Omar Khayyam', 'رباعیات', 'زندگی', ARRAY['امید', 'صبر', 'قناعت']),

('گر تو بر آن که دوست دارم بگذری / من بر همان که هست دارم بگذرم', 'If you pass by the one I love / I will pass by the one who exists', 'نظامی گنجوی', 'Nizami', 'خمسه', 'عشق', ARRAY['عشق', 'وفاداری', 'ایثار']),

('دوست آن باشد که گیرد دست دوست / در پریشان حالی و درماندگی', 'A friend is one who takes a friend''s hand / In times of trouble and helplessness', 'سعدی شیرازی', 'Saadi', 'بوستان', 'دوستی', ARRAY['دوستی', 'وفاداری', 'کمک']),

('عمر عزیز است مفروش به ارزان / که دگر بار نخواهی توانست آن', 'Life is precious, don''t sell it cheap / For you won''t be able to buy it again', 'حافظ شیرازی', 'Hafez', 'دیوان حافظ', 'زندگی', ARRAY['زندگی', 'ارزش', 'وقت']),

('صبح صادق بشکافد پرده شب را / نور خورشید بتاباند جهان را', 'The true dawn will tear the veil of night / The sun''s light will illuminate the world', 'مولانا جلال‌الدین رومی', 'Rumi', 'مثنوی معنوی', 'امید', ARRAY['امید', 'نور', 'تجدید']),

('در ازل پرتو حسنت ز تجلی دم زد / عشق پیدا شد و آتش به همه عالم زد', 'In eternity, the ray of your beauty spoke from manifestation / Love appeared and set fire to the whole world', 'حافظ شیرازی', 'Hafez', 'دیوان حافظ', 'عشق', ARRAY['عشق الهی', 'زیبایی', 'عرفان']);
